mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO file
          ( original_name
          , name
          , `path`
          , `type`
          , byte_size
          , create_user
          , create_datetime
          , update_user
          , update_datetime
          )
     VALUES
          ( #{original_name}
          , #{name}
          , #{path}
          , #{type}
          , #{size}
          , 1
          , SYSDATE()
          , 1
          , SYSDATE()
          )

mapper.update:
UPDATE file
   SET original_name = #{original_name}
     , name = #{name}
     , `path` = #{path}
     , `type` = #{type}
     , byte_size = #{size}
     , update_user = 1
     , update_datetime = SYSDATE()
 WHERE id = #{file_id}
