mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO file
          ( original_name
          , name
          , `path`
          , `type`
          , byte_size
          , create_user
          , create_datetime
          )
     VALUES
          ( #{original_name}
          , #{name}
          , #{path}
          , #{type}
          , #{size}
          , 1
          , SYSDATE()
          )
